import { useState } from "react";

export function FormSetCalorie({selectedMember}){

    const [exercise, setExercise] = useState("jogging");
    const [time, setTime] = useState("");

    let result = 0;
    console.log("result: " + result);
    
    function calculateCalories(){
        console.log("Function got triggered!");
        let faktor = 0;
        
        switch (exercise) {
            default: break;
            case "resistance training":
                faktor = 3.5
                break;
            case "calisthenics":
                faktor = 3.8
                break;
            case "swimming":
                faktor = 2.5
                break;
            case "bicycling":
                faktor = 3
                break;
            case "jogging":
                faktor = 7
                break;
            case "climbing":
                faktor = 8
                break;     
        }
         
        result = (faktor * Number(selectedMember.weight) * Number(time)) / 60;
        return result;
         
    }

    return(
        <form className="form-add-member">
            <h3> Exercise of {selectedMember.name}</h3>
            <label>üèÉ Set Exercise</label>
            <select value={exercise} onChange={(e) => setExercise(e.target.value)}>
                <option value="resistance training">resistance training</option>
                <option value="calisthenics">calisthenics</option>
                <option value="swimming">swimming</option>
                <option value="bicycling">bicycling</option>
                <option value="jogging">jogging</option>
                <option value="climbing">climbing</option>
            </select>
            <label onClick={calculateCalories}>‚è≤Ô∏è Duration (min)</label>
            <input type="text" value={time} onChange={(e) => setTime(e.target.value)}></input> 
            <label>‚öñÔ∏è Bodyweight (kg)</label>
            <input type="text" disabled value={selectedMember.weight}></input> 
            <label>üèÖ Calorie burned</label>
            <input type="text" disabled value={result.toString()}></input>  

        </form>
    )
}
