export default function Member({member, sortedMembers, selectedMember, onHandleSelected}){
    
    
    console.log("selected: " + selectedMember?.id);
    console.log("member.id: " + member.id);
    const isSelected = selectedMember?.id === member.id
    
    console.log("isSelected: " + isSelected)
    
    if(sortedMembers.indexOf(member) === 0){
        return(
            <li>
            <img className="crown" src="crown.svg" alt="crown"></img>
            <h3 className="h3-king"><span>ðŸ’ª{member.name}</span></h3>
            <img className="img-king" src={member.image} alt={member.image}></img>
            <p className="pWeight">{"weight (in kg): " + member.weight + member.id}</p>
            <p className="pcR">{"CalorieRecord: " + member.calorieRecord}</p>
            <button className="button" onClick={() => onHandleSelected(member)}>Select</button>
        </li>
        )
    }
    else{
    return(
        <li>
            <h3>{member.name}</h3>
            <img src={member.image} alt={member.image}></img>
            <p className="pWeight">{"weight (in kg): " + member.weight + member.id}</p>
            <p className="pcR">{"CalorieRecord: " + member.calorieRecord}</p>
            <button className="button" onClick={(id) => onHandleSelected(id)}>Select</button>
        </li>
    )
}
}
